(()=>{"use strict";const e=document.querySelector("input"),t=document.querySelector("button"),n=document.querySelector(".city-name"),o=document.querySelector(".temp"),c=document.querySelector(".feels"),r=document.querySelector(".description"),i=document.querySelector(".wind-speed");document.querySelector(".time"),t.addEventListener("click",(async function(){const t=e.value,a=await async function(e){try{const t=await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${e}&APPID=d3f06a0f8adbf844ad69d2ba2196288c&units=imperial`,{mode:"cors"});return console.log("hi"),await t.json()}catch(e){console.error}}(t);n.textContent=a.name,o.textContent="Temperature: "+a.main.temp,c.textContent="Feels Like: "+a.main.feels_like,r.textContent="Description: "+a.weather[0].description,i.textContent="Wind speed: "+a.wind.speed+"mph",console.log(a)}))})();